// src/axios.js

import axios from 'axios';
import { Message } from 'element-ui'; // 如果使用 Element UI 的消息提示

// 创建一个 Axios 实例
const instance = axios.create({
  baseURL: 'http://localhost:5000', // 后端 API 的基础 URL，按需修改
  timeout: 5000, // 请求超时时间
});

// 请求拦截器：在每个请求发送之前做一些处理
instance.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('access_token');
    if (token) {
      config.headers['Authorization'] = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

// 响应拦截器：在每个响应到达之前做一些处理
instance.interceptors.response.use(
  (response) => {
    // 可以在这里处理全局的响应
    return response;
  },
  (error) => {
    if (error.response) {
      // 根据需求处理不同的响应错误
      const { status, data } = error.response;
      if (status === 401) {
        Message.error('未授权，请重新登录');
        // 可以添加跳转到登录页的逻辑
      } else if (status === 400) {
        Message.error(data.message || '请求错误');
      } else if (status === 500) {
        Message.error('服务器内部错误');
      } else {
        Message.error(data.message || '发生错误');
      }
    } else if (error.request) {
      Message.error('未收到服务器响应，请检查网络连接');
    } else {
      Message.error(error.message || '发生错误');
    }
    return Promise.reject(error);
  }
);

export default instance;
