import Vue from 'vue';
import App from './App.vue';
import ElementUI from 'element-ui';
import 'element-ui/lib/theme-chalk/index.css';
import axios from 'axios';
import VueRouter from 'vue-router';
import HomePage from './components/HomePage.vue';
import Login from './components/Login.vue';
import Register from './components/Register.vue';
import ProblemSet from './components/ProblemSet.vue';
import ProblemList from './components/ProblemList.vue';
import ContestPage from './components/Contest.vue';
import ContestDetail from './components/ContestDetail.vue'; // 新增比赛详情页面
import CreateContest from './components/CreateContest.vue'; // 新增发布比赛页面
import DiscussionPage from './components/Discussion.vue';
import SquarePage from './components/Square.vue'; // 新增广场页面
import UserProfile from './components/UserProfile.vue'; // 新增个人主页页面
import ProblemDetail from './components/ProblemDetail.vue'; // 新增题目页面
import UploadProblem from './components/UploadProblem.vue'; // 新增上传题目页面
import SolutionDetail from './components/SolutionDetail.vue'; // 新增题解页面

Vue.config.productionTip = false;

Vue.use(ElementUI);
Vue.use(VueRouter);
Vue.prototype.$axios = axios;

// 配置axios的基础URL
axios.defaults.baseURL = 'http://127.0.0.1:5000';

// 配置路由
const routes = [
  { path: '/', component: HomePage },
  { path: '/login', component: Login },
  { path: '/register', component: Register },
  { path: '/problemset', component: ProblemSet },
  { path: '/problemlist', component: ProblemList },
  { path: '/contest', component: ContestPage },
  { path: '/contest/:id', component: ContestDetail }, // 动态路由配置
  { path: '/create-contest', component: CreateContest }, // 新增发布比赛页面路由
  { path: '/discussion', component: DiscussionPage },
  { path: '/square', component: SquarePage },
  { path: '/profile/:userId', component: UserProfile }, // 动态路由配置
  { path: '/problem/:problemId', component: ProblemDetail }, // 动态路由配置
  { path: '/upload', component: UploadProblem }, // 上传题目页面
  { path: '/solution/:problemId', component: SolutionDetail } // 题解页面
];

const router = new VueRouter({
  routes
});

new Vue({
  render: h => h(App),
  router
}).$mount('#app');
