// src/router.js

import Vue from 'vue';
import VueRouter from 'vue-router';
import HomePage from './components/HomePage.vue';
import Login from './components/Login.vue';
import Register from './components/Register.vue';
import ProblemSet from './components/ProblemSet.vue';
import ProblemList from './components/ProblemList.vue';
import ContestPage from './components/Contest.vue';
import ContestDetail from './components/ContestDetail.vue';
import CreateContest from './components/CreateContest.vue';
import DiscussionPage from './components/Discussion.vue';
import SquarePage from './components/Square.vue';
import UserProfile from './components/UserProfile.vue';
import ProblemDetail from './components/ProblemDetail.vue';
import UploadProblem from './components/UploadProblem.vue';
import SolutionDetail from './components/SolutionDetail.vue';
import CreateDiscussion from './components/CreateDiscussion.vue';
import CreateProblemList from './components/CreateProblemList.vue';
import SubmitCode from './components/SubmitCode.vue';
import Dashboard from './components/Dashboard.vue'; // 确保 Dashboard 组件名称为多字

Vue.use(VueRouter);

const routes = [
  { path: '/', component: HomePage },
  { path: '/login', component: Login },
  { path: '/register', component: Register },
  { path: '/problemset', component: ProblemSet },
  { path: '/problemlist', component: ProblemList },
  { path: '/contest', component: ContestPage },
  { path: '/contest/:id', component: ContestDetail },
  { path: '/create-contest', component: CreateContest },
  { path: '/discussion', component: DiscussionPage },
  { path: '/create-discussion', component: CreateDiscussion },
  { path: '/square', component: SquarePage },
  { path: '/profile/:userId', component: UserProfile },
  { path: '/problem/:problemId', component: ProblemDetail },
  { path: '/upload', component: UploadProblem },
  { path: '/solution/:problemId', component: SolutionDetail },
  { path: '/create-problemlist', component: CreateProblemList },
  { path: '/submit/:problemId', component: SubmitCode },
  { path: '/dashboard', component: Dashboard, meta: { requiresAuth: true } },
];

const router = new VueRouter({
  mode: 'history',
  routes
});

// 路由守卫
router.beforeEach((to, from, next) => {
  const requiresAuth = to.matched.some(record => record.meta.requiresAuth);
  const token = localStorage.getItem('access_token');

  if (requiresAuth && !token) {
    next('/login');
  } else {
    next();
  }
});

// 全局导航守卫，触发全局事件以更新组件状态
router.afterEach(() => {
  const event = new Event('user-login-status-change');
  window.dispatchEvent(event);
});

export default router;
